import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import youtube from "./api/youtube";
import unescape from "lodash/unescape";
import debounce from "lodash/debounce";
import mockResponse from "./mockData";
import CircularProgress from "@material-ui/core/CircularProgress";

// console.log(process.env.REACT_APP_YOUTUBE_API_KEY)
// Channel Id = UCu2YhywZvs7pXUEL3RCUEaA
// API key = AIzaSyAbnlDjtTzRH0MXRtdWapejsD5V0yzKLS0
// https://www.googleapis.com/youtube/v3/search?order=date&type=video&part=snippet&channelId=UCu2YhywZvs7pXUEL3RCUEaA&maxResults=15&key=AIzaSyAbnlDjtTzRH0MXRtdWapejsD5V0yzKLS0
// Page token = https://www.googleapis.com/youtube/v3/search?order=date&type=video&part=snippet&pageToken=CA8QAA&channelId=UCu2YhywZvs7pXUEL3RCUEaA&maxResults=15&key=AIzaSyAbnlDjtTzRH0MXRtdWapejsD5V0yzKLS0

const Videos = ({ videos }) => {


  if (!videos) {
    return (
      <div>
        <CircularProgress></CircularProgress>
      </div>
    )
  }

  if (videos.length === 0) {
    return <div>No videos found. Try to search for another song or artist.</div>
  }


  return (
    <div>
      <ul>
        {videos.map((video) => (
          <li key={video.id.videoId}>
            {unescape(video.snippet.title)}
          </li>
        ))}
      </ul>
    </div>
  );
};

const App = () => {
  const [videos, setVideos] = useState([]);
  const [videoFilter, setVideoFilter] = useState("");
  const [nextPageToken, setNextPageToken] = useState("");
  const [prevPageToken, setPrevPageToken] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  // const [videos, setVideos] = useState(mockResponse.data.items);
  // const [nextPageToken, setNextPageToken] = useState(mockResponse.data.prevPageToken);
  // const [prevPageToken, setPrevPageToken] = useState(mockResponse.data.prevPageToken);

  const delayedGetVideos = useCallback(debounce((value) => getVideos("", value), 500), []);

  const handleChange = (event) => {
    console.log(event.target.value);
    const {value} = event.target;
    setVideoFilter(value);
    delayedGetVideos(value);
  };

  const getVideos = (page, filterValue) => {
    console.log("filterValue on...", filterValue);

    const baseURL = "https://www.googleapis.com/youtube/v3/search";
    // const order = orderTest ? `?order=${orderTest}` : "?order=date";
    const order = "?order=date";
    const type = "&type=video"; 
    const part = "&part=snippet";
    const channelId = "&channelId=UCu2YhywZvs7pXUEL3RCUEaA";
    const maxResults = "&maxResults=15";
    const q = `&q=${filterValue}`;
    const pageToken = page ? `&pageToken=${page}` : "";
    const key = `&key=${process.env.REACT_APP_YOUTUBE_API_KEY}`;
    const url = `${baseURL}${order}${type}${part}${channelId}${maxResults}${q}${pageToken}${key}`;

    axios
      .get(url)
      .then((response) => {
        setVideos(response.data["items"]);
        setNextPageToken(response.data.nextPageToken);
        setPrevPageToken(response.data.prevPageToken);
        console.log("response", response);
        setIsLoading(false);
      })
      .catch((error) => {
        console.log(error);

      });
  };

  // Haetaan videot etusivulle
  // Kuuntelee videoFilter arvoa: efektin sis채ll채 olevat funktiot k채ynnistyv채t, kun
  // videoFilterin arvo muuttuu
  useEffect(() => {


    getVideos("", "");

  }, []);

  const onNextClick = () => {
    getVideos(nextPageToken, "");
  };

  const onPrevClick = () => {
    getVideos(prevPageToken, "");
  };

  console.log("render", videos.length, "videos");



  return (
    <div>
      <input value={videoFilter} onChange={handleChange} />      
      <Videos videos={videos} />
      <button onClick={onPrevClick}>Previous</button>
      <button onClick={onNextClick}>Next</button>
    </div>
  );
};

export default App;
