import React, { useState, useEffect } from "react";
import axios from "axios";
import youtube from "./api/youtube";
import unescape from "lodash/unescape";

// console.log(process.env.REACT_APP_YOUTUBE_API_KEY)
// Channel Id = UCu2YhywZvs7pXUEL3RCUEaA
// API key = AIzaSyAbnlDjtTzRH0MXRtdWapejsD5V0yzKLS0
// https://www.googleapis.com/youtube/v3/search?order=date&type=video&part=snippet&channelId=UCu2YhywZvs7pXUEL3RCUEaA&maxResults=15&key=AIzaSyAbnlDjtTzRH0MXRtdWapejsD5V0yzKLS0
// Page token = https://www.googleapis.com/youtube/v3/search?order=date&type=video&part=snippet&pageToken=CA8QAA&channelId=UCu2YhywZvs7pXUEL3RCUEaA&maxResults=15&key=AIzaSyAbnlDjtTzRH0MXRtdWapejsD5V0yzKLS0

const Videos = ({ videos }) => {
  return (
    <div>
      <ul>
        {videos.map((video) => (
          <li key={video.id.videoId}>{unescape(video.snippet.title)}</li>
        ))}
      </ul>
    </div>
  );
};

const App = () => {
  const [videos, setVideos] = useState([]);
  const [videoFilter, setVideoFilter] = useState("");
  const [nextPageToken, setNextPageToken] = useState("");

  const handleChange = (event) => {
    console.log(event.target.value);
    setVideoFilter(event.target.value);
  };

  const videosToShow = videoFilter
    ? videos.filter((video) =>
        video.snippet.title.toLowerCase().includes(videoFilter.toLowerCase())
      )
    : videos;

  const getVideos = (pageToken) => {
    const url = "https://www.googleapis.com/youtube/v3/search" +
    "?order=date" +
    "&type=video" +
    "&part=snippet" +
    "&channelId=UCu2YhywZvs7pXUEL3RCUEaA" +
    "&maxResults=15" +
    `&key=${process.env.REACT_APP_YOUTUBE_API_KEY}`

    const finalUrl = pageToken ? `${url}&pageToken=${pageToken}` : url

    axios
    .get(
      finalUrl
    )
    .then((response) => {
      setVideos(response.data["items"]);
      console.log("response", response)
      setNextPageToken(response.data.nextPageToken);
    })
    .catch((error) => {
      console.log(error);
    });
  }

  const onNextClick = () => {
    getVideos(nextPageToken);
  }

  useEffect(() => {
    getVideos("");
  }, []);

  console.log("render", videos.length, "videos");
  console.log(videos);
  console.log(nextPageToken);


  return (
    <div>
      <input value={videoFilter} onChange={handleChange} />
      <Videos videos={videosToShow} />
      <button>Previous</button>
      <button onClick={onNextClick}>Next</button>
    </div>
  );
};

export default App;
